<?phpnamespace TheRayCode\Prototype;include_once ("Page.php");include_once ("Author.php");/** * The client code. */function clientCode(){    $author = new Author("John Smith<br/>");    $page = new Page("<b>Tip of the day</b><br/>", "Keep calm and carry on.<br/>", $author);    // ...    $page->addComment("<b>Nice tip, thanks!</b><br/>");    // ...    $draft = clone $page;    echo "<b>Dump of the clone</b>. Note that the author is now referencing two objects.<br/><br/>";    print_r($draft);}clientCode();echo "<br/><br/>";echo "The Ray Code is AWESOME!!!<br/>";